import React, { useState, useEffect } from 'react';
import QuestionList from "./pages/QuestionsListPage";
import AddQuestionForm from './pages/AddQuestionForm';
import './App.css';

function App() {
  const [questions, setQuestions] = useState([]);

  // Function to fetch questions from the database
  const fetchQuestions = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/user/getquestion?question_id=1');
      if (!response.ok) {
        throw new Error('Failed to fetch questions');
      }
      const data = await response.json();
      setQuestions(data.responses);
    } catch (error) {
      console.error('Error fetching questions:', error);
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, []);

  const addQuestion = async (newQuestion) => {
    try {
      console.log(JSON.stringify(newQuestion))
      const response = await fetch('http://127.0.0.1:8000/api/user/postquestion', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': 'http://localhost:3000',
          "Access-Control-Allow-Credentials": true
        },
        body: JSON.stringify(newQuestion),
      });
      if (!response.ok) {
        throw new Error('Failed to add question');
      }
      const addedQuestion = await response.json();
      setQuestions([...questions, addedQuestion]);
    } catch (error) {
      console.error('Error adding question:', error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Question List</h1>
        <AddQuestionForm onAddQuestion={addQuestion} />
        <QuestionList questions={questions} />
      </header>
    </div>
  );
}

export default App;
