import React, { useState, useEffect } from 'react';
import QuestionList from "./pages/QuestionsListPage";
import AddQuestionForm from './pages/AddQuestionForm';
import './App.css';
import Header from './components/Header';

import { createUser } from './components/contract';

function App() {
  const [questions, setQuestions] = useState([]);
  const [pubkey,setpubkey]=useState("");
  // Function to fetch questions from the database
  const fetchQuestions = async () => {
    try {
      const response = await fetch('http://127.0.0.1:4000/api/user/getquestion?question_id=1');
      if (!response.ok) {
        throw new Error('Failed to fetch questions');
      }
      const data = await response.json();
      setQuestions(data.responses);
    } catch (error) {
      console.error('Error fetching questions:', error);
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, []);

  const addQuestion = async (newQuestion) => {
    try {
      console.log(JSON.stringify(newQuestion))
      const response = await fetch('http://127.0.0.1:4000/api/user/postquestion', {
        method: 'POST',
        headers: {

          'Access-Control-Allow-Origin': '*',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newQuestion),
      });
      if (!response.ok) {
        throw new Error('Failed to add question');
      }
      const addedQuestion = await response.json();
      setQuestions([...questions, addedQuestion]);
    } catch (error) {
      console.error('Error adding question:', error);
    }
  };

  return (
    <div className="App">
      button
      <Header setpubkey={setpubkey}/>
      <header className="App-header">
        <h1>Question List</h1>
        <AddQuestionForm onAddQuestion={addQuestion} />
        <QuestionList questions={questions} />
      </header>
    </div>
  );
}

export default App;
